const a=o=>{document.querySelectorAll(o).forEach(t=>{const n=t.querySelector("div");if(n&&n.classList.contains("plaintext"))return;const e=document.createElement("button");e.className="copy-button",e.innerText="Copy",t.parentNode.style.position="relative",t.parentNode.appendChild(e),e.addEventListener("click",async()=>{const i=t.innerText;try{await navigator.clipboard.writeText(i),e.innerText="Copied!",e.classList.add("copied")}catch{e.innerText="Failed"}setTimeout(()=>{e.innerText="Copy",e.classList.remove("copied")},2e3)})})};export{a};
